/* eslint-disable prefer-template */
import React from 'react';
import { _ } from 'splunk-ui/util/i18n';
import Markdown from 'splunk-ui/components/Markdown';

function I18nExample() {
    const text = _(
        '### Attention!\n' +
        'Contents here need to be translated, thank you, the translation crew who is reading ' +
        'this paragraph.\n\n' +
        'By the way, please leave the url of this [Link](http://mypreciouslink.splunk) untouched.\n'
    );
    return <Markdown text={text} />;
}

export default I18nExample;
