import React, { Component } from 'react';
import Button from 'splunk-ui/components/Button';
import Modal from 'splunk-ui/components/Modal';

class Basic extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = {
            open: false,
        };
    }

    handleRequestOpen = () => {
        this.setState({
            open: true,
        });
    }

    handleRequestClose = () => {
        this.setState({
            open: false,
        });
    }

    render() {
        return (
            <div>
                <Button onClick={this.handleRequestOpen} label="Click me" />
                <Modal onRequestClose={this.handleRequestClose} open={this.state.open}>
                    <Modal.Body>
                        Just a basic Modal. No thrills at all.
                    </Modal.Body>
                </Modal>
            </div>
        );
    }
}

export default Basic;
