import React, { Component } from 'react';
import Multiselect from 'splunk-ui/components/Multiselect';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            values: ['1', '2'],
        };
    }

    handleChange = (e, { values }) => {
        this.setState({ values });
    }

    render() {
        return (
            <Multiselect values={this.state.values} onChange={this.handleChange}>
                <Multiselect.Option
                    label="Up"
                    value="1"
                />
                <Multiselect.Option
                    label="Up and Down"
                    value="8"
                />
                <Multiselect.Option
                    label="Down"
                    value="2"
                />
                <Multiselect.Option
                    label="Down and Under"
                    value="3"
                />
                <Multiselect.Divider />
                <Multiselect.Option
                    label="Left"
                    value="4"
                />
                <Multiselect.Option
                    label="Left and Over"
                    value="5"
                />
                <Multiselect.Divider />
                <Multiselect.Option
                    label="Right"
                    value="6"
                />
                <Multiselect.Option
                    label="Right and Under"
                    value="7"
                />
                <Multiselect.Option
                    label="Eight"
                    value="8"
                />
                <Multiselect.Option
                    label="Nine"
                    value="9"
                />
                <Multiselect.Option
                    label="Ten"
                    value="10"
                />
                <Multiselect.Option
                    label="Eleven"
                    value="11"
                />
            </Multiselect>
        );
    }
}

export default Example;
