import React, { Component } from 'react';
import Multiselect from 'splunk-ui/components/Multiselect';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            values: ['up', 'upAndDown', 'apples'],
        };
    }

    handleChange = (e, { values }) => {
        this.setState({ values });
    }

    render() {
        return (
            <Multiselect
                allowNewValues
                values={this.state.values}
                onChange={this.handleChange}
            >
                <Multiselect.Option
                    label="Up"
                    value="up"
                />
                <Multiselect.Option
                    label="Up and Down"
                    value="upAndDown"
                />
                <Multiselect.Option
                    label="Down"
                    value="down"
                />
                <Multiselect.Option
                    label="Down and Under"
                    value="downAndUnder"
                />
                <Multiselect.Divider />
                <Multiselect.Option
                    label="Left"
                    value="left"
                />
                <Multiselect.Option
                    label="Left and Over"
                    value="leftAndOver"
                />
                <Multiselect.Divider />
                <Multiselect.Option
                    label="Right"
                    value="right"
                />
                <Multiselect.Option
                    label="Right and Under"
                    value="rightAndUnder"
                />
                <Multiselect.Option
                    label="Eight"
                    value="eigth"
                />
                <Multiselect.Option
                    label="Nine"
                    value="nine"
                />
                <Multiselect.Option
                    label="Ten"
                    value="ten"
                />
                <Multiselect.Option
                    label="Eleven"
                    value="eleven"
                />
            </Multiselect>
        );
    }
}

export default Example;
