import React, { Component } from 'react';
import Button from 'splunk-ui/components/Button';
import Popover from 'splunk-ui/components/Popover';

class Example extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = {
            open: false,
            anchor: null,
        };
    }

    handleMount = (component) => {
        this.setState({
            anchor: component,
        });
    }

    handleOpen = () => {
        this.setState({
            open: true,
        });
    }

    handleRequestClose = () => {
        this.setState({
            open: false,
        });
    }

    render() {
        const { anchor, open } = this.state;
        return (
            <div>
                <Button onClick={this.handleOpen} ref={this.handleMount} label="Click Me" />
                <Popover
                    open={open}
                    anchor={anchor}
                    onRequestClose={this.handleRequestClose}
                    appearance="light"
                >
                    <div style={{ padding: '20px', width: '300px' }}>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque vestibulum
                        commodo diam, eu consectetur nulla tincidunt a. Maecenas eget fermentum
                        tellus. Nulla suscipit a tellus vel varius. Vestibulum eu elit a metus
                        varius venenatis eget ut risus. Duis suscipit in arcu volutpat facilisis.
                        Quisque eu dictum metus. Aenean commodo cursus sollicitudin. Etiam at
                        posuere ligula. Sed sapien massa, laoreet a cursus at, malesuada vel mi.
                        Duis maximus orci est, facilisis blandit urna at.
                    </div>
                </Popover>
            </div>
        );
    }
}

export default Example;
