import React, { Component } from 'react';
import RadioBar from 'splunk-ui/components/RadioBar';

class Basic extends Component {
    constructor(...args) {
        super(...args);
        this.state = {
            value: 3,
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <RadioBar onChange={this.handleChange} value={this.state.value}>
                <RadioBar.Option value={1} label="one" />
                <RadioBar.Option value={2} label="two" />
                <RadioBar.Option value={3} label="threethreethree" />
                <RadioBar.Option value={4} label="four" />
            </RadioBar>
        );
    }
}

export default Basic;
