import React, { Component } from 'react';
import RadioList from 'splunk-ui/components/RadioList';
import Text from 'splunk-ui/components/Text';

class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: 2,
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <RadioList value={this.state.value} onChange={this.handleChange}>
                <RadioList.Option value={1}>One</RadioList.Option>
                <RadioList.Option value={2}>Two</RadioList.Option>
                <RadioList.Option value={3}>
                    Three or <Text inline placeholder="value" style={{ marginTop: '-4px' }} />
                </RadioList.Option>
                <RadioList.Option value={4}>Four</RadioList.Option>
            </RadioList>
        );
    }
}

export default Example;
