import React from 'react';
import Scroll from 'splunk-ui/components/Scroll';

function Uncontrolled() {
    const styles = {
        border: '1px solid #ccc',
        height: '400px',
        width: '400px',
    };

    return (
        <Scroll
            stopScrollPropagation
            style={styles}
        >
            { new Array(300).join('Repeating words. ') }
        </Scroll>
    );
}

export default Uncontrolled;
