import React, { Component } from 'react';
import Select from 'splunk-ui/components/Select';

class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            valueA: '3',
            valueB: '3',
        };
    }

    handleChangeA = (e, { value }) => {
        this.setState({ valueA: value });
    }

    handleChangeB = (e, { value }) => {
        this.setState({ valueB: value });
    }

    render() {
        return (
            <div>
                <Select
                    appearance="pill"
                    value={this.state.valueA}
                    onChange={this.handleChangeA}
                >
                    <Select.Option label="Up" value="1" />
                    <Select.Option label="Down" value="2" />
                    <Select.Option label="Left" value="3" />
                    <Select.Option label="Right" value="4" />
                </Select>
                <Select
                    appearance="primary"
                    value={this.state.valueB}
                    style={{ marginLeft: '20px' }}
                    onChange={this.handleChangeB}
                >
                    <Select.Option label="Up" value="1" />
                    <Select.Option label="Down" value="2" />
                    <Select.Option label="Left" value="3" />
                    <Select.Option label="Right" value="4" />
                </Select>
            </div>
        );
    }
}


export default Example;
