import React, { Component } from 'react';
import TriangleDownSmall from 'splunk-ui/icons/TriangleDownSmall';
import TriangleLeftSmall from 'splunk-ui/icons/TriangleLeftSmall';
import TriangleRightSmall from 'splunk-ui/icons/TriangleRightSmall';
import TriangleUpSmall from 'splunk-ui/icons/TriangleUpSmall';
import Select from 'splunk-ui/components/Select';

class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: '2',
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <Select value={this.state.value} onChange={this.handleChange}>
                <Select.Option
                    label="Up"
                    icon={<TriangleUpSmall />}
                    description="Go over"
                    descriptionPosition="right"
                    value="1"
                />
                <Select.Option
                    label="Down"
                    icon={<TriangleDownSmall />}
                    description="Go under"
                    descriptionPosition="right"
                    value="2"
                />
                <Select.Divider />
                <Select.Option
                    label="Left"
                    icon={<TriangleLeftSmall />}
                    description="Stage right"
                    descriptionPosition="right"
                    value="3"
                />
                <Select.Option
                    label="Right"
                    icon={<TriangleRightSmall />}
                    description="Stage left"
                    descriptionPosition="right"
                    value="4"
                />
            </Select>
        );
    }
}

export default Example;
