import React, { Component } from 'react';
import Select from 'splunk-ui/components/Select';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: '1',
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <Select value={this.state.value} onChange={this.handleChange}>
                <Select.Option
                    label="Up"
                    value="1"
                    description="Go over."
                />
                <Select.Option
                    label="Down"
                    value="2"
                    description="Go under."
                />
                <Select.Option
                    label="Left"
                    value="3"
                    description="Stage right"
                    descriptionPosition="right"
                />
                <Select.Option
                    label="Right"
                    value="4"
                    description="Stage left"
                    descriptionPosition="right"
                />
            </Select>
        );
    }
}

export default Example;
