import React, { Component } from 'react';
import Select from 'splunk-ui/components/Select';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: '1',
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <Select value={this.state.value} filter onChange={this.handleChange}>
                <Select.Option
                    label="Up"
                    value="1"
                />
                <Select.Option
                    label="Up and Down"
                    value="8"
                />
                <Select.Option
                    label="Down"
                    value="2"
                />
                <Select.Option
                    label="Down and Under"
                    value="3"
                />
                <Select.Divider />
                <Select.Option
                    label="Left"
                    value="4"
                />
                <Select.Option
                    label="Left and Over"
                    value="5"
                />
                <Select.Divider />
                <Select.Option
                    label="Right"
                    value="6"
                />
                <Select.Option
                    label="Right and Under"
                    value="7"
                />
            </Select>
        );
    }
}

export default Example;
