import React, { Component } from 'react';
import TriangleRightSmall from 'splunk-ui/icons/TriangleRightSmall';
import TriangleUpSmall from 'splunk-ui/icons/TriangleUpSmall';
import Select from 'splunk-ui/components/Select';


class Example extends Component {
    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: '1',
        };
    }

    handleChange = (e, { value }) => {
        this.setState({ value });
    }

    render() {
        return (
            <Select
                prefixLabel="Direction"
                value={this.state.value}
                onChange={this.handleChange}
            >
                <Select.Option
                    label="Up"
                    value="1"
                    icon={<TriangleUpSmall />}
                />
                <Select.Option
                    label="Right"
                    value="2"
                    icon={<TriangleRightSmall />}
                />
            </Select>
        );
    }
}

export default Example;
