import React, { Component } from 'react';
import ControlGroup from 'splunk-ui/components/ControlGroup';
import Slider from 'splunk-ui/components/Slider';
import Text from 'splunk-ui/components/Text';

class Controlled extends Component {

    constructor(props, ...rest) {
        super(props, ...rest);

        this.state = {
            value: 300,
        };
    }

    handleSliderChange = (e, { value }) => {
        this.setState({
            value,
        });
    }

    render() {
        return (
            <ControlGroup label="Controls">
                <Slider
                    inline
                    min={100}
                    max={500}
                    step={25}
                    onChange={this.handleSliderChange}
                    value={this.state.value}
                />
                <Text inline value={this.state.value.toString()} />
            </ControlGroup>
        );
    }
}

export default Controlled;
