import React, { Component } from 'react';
import Button from 'splunk-ui/components/Button';
import StepBar from 'splunk-ui/components/StepBar';

class Basic extends Component {
    constructor(props, context) {
        super(props, context);
        this.stepLength = 4;
        this.state = { activeStepId: 0 };
    }

    handlePrevious = () => {
        this.setState({ activeStepId: Math.max(0, this.state.activeStepId - 1) });
    }

    handleNext = () => {
        this.setState({ activeStepId: Math.min(this.stepLength - 1, this.state.activeStepId + 1) });
    }

    render() {
        const notComplete = this.state.activeStepId < this.stepLength - 1;
        return (
            <div>
                <StepBar activeStepId={this.state.activeStepId}>
                    <StepBar.Step label="One" />
                    <StepBar.Step label="Two" />
                    <StepBar.Step label="Three" />
                    <StepBar.Step label="Complete" />
                </StepBar>
                {notComplete && (
                    <Button
                        label="&lt;"
                        appearance="default"
                        disabled={this.state.activeStepId === 0}
                        onClick={this.handlePrevious}
                    />
                )}
                {notComplete && (
                    <Button
                        label="Next"
                        appearance="primary"
                        onClick={this.handleNext}
                    />
                )}
            </div>
        );
    }
}

export default Basic;
