import React, { Component } from 'react';
import { includes, without } from 'lodash';
import Switch from 'splunk-ui/components/Switch';

class Basic extends Component {
    constructor(...args) {
        super(...args);
        this.state = {
            values: [1, 2, 5, 6, 9, 10],
        };
    }

    handleClick = (e, { value }) => {
        const prevValues = this.state.values;
        if (includes(prevValues, value)) {
            this.setState({
                values: without(prevValues, value),
            });
        } else {
            this.setState({
                values: prevValues.concat(value),
            });
        }
    }

    render() {
        const selectedValues = this.state.values;

        const RadioSwitches = [1, 2, 3, 4].map(value => (
            <Switch
                key={value}
                value={value}
                onClick={this.handleClick}
                selected={includes(selectedValues, value)}
                appearance="radio"
                size="small"
            >
                {value}
            </Switch>
        ));

        const CheckboxSwitches = [5, 6, 7, 8].map(value => (
            <Switch
                key={value}
                value={value}
                onClick={this.handleClick}
                selected={includes(selectedValues, value)}
                appearance="checkbox"
                size="small"
            >
                {value}
            </Switch>
        ));

        const ToggleSwitches = [9, 10, 11, 12].map(value => (
            <Switch
                key={value}
                value={value}
                onClick={this.handleClick}
                selected={includes(selectedValues, value)}
                appearance="toggle"
                size="small"
            >
                {value}
            </Switch>
        ));

        return (
            <div style={{ display: 'flex' }}>
                <div>
                    {RadioSwitches}
                </div>
                <div style={{ marginLeft: '100px' }}>
                    {CheckboxSwitches}
                </div>
                <div style={{ marginLeft: '100px' }}>
                    {ToggleSwitches}
                </div>
            </div>
        );
    }
}

export default Basic;
