import React, { Component } from 'react';
import TabBar from 'splunk-ui/components/TabBar';

class Basic extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = { activeTabId: 'one' };
    }

    handleChange = (e, data) => {
        this.setState({ activeTabId: data.selectedTabId });
    }

    render() {
        return (
            <div>
                <TabBar activeTabId={this.state.activeTabId} onChange={this.handleChange}>
                    <TabBar.Tab label="One" tabId="one" />
                    <TabBar.Tab label="Two" tabId="two" />
                    <TabBar.Tab label="Three" tabId="three" />
                </TabBar>
            </div>
        );
    }
}

export default Basic;
