import React, { Component } from 'react';
import AlertCircle from 'splunk-ui/icons/AlertCircle';
import Data from 'splunk-ui/icons/Data';
import DistributedEnvironment from 'splunk-ui/icons/DistributedEnvironment';
import TabBar from 'splunk-ui/components/TabBar';

class Icons extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = { activeTabId: 'alert' };
    }

    handleChange = (e, data) => {
        this.setState({ activeTabId: data.selectedTabId });
    }

    render() {
        return (
            <div>
                <TabBar activeTabId={this.state.activeTabId} onChange={this.handleChange}>
                    <TabBar.Tab label="Alert" icon={<AlertCircle />} tabId="alert" />
                    <TabBar.Tab label="Data" icon={<Data />} tabId="data" />
                    <TabBar.Tab
                        label="Distributed"
                        icon={<DistributedEnvironment />}
                        tabId="dist"
                    />
                </TabBar>
            </div>
        );
    }
}

export default Icons;
