import React, { Component } from 'react';
import DL from 'splunk-ui/components/DefinitionList';
import Link from 'splunk-ui/components/Link';
import List from 'splunk-ui/components/List';
import P from 'splunk-ui/components/Paragraph';
import TabLayout from 'splunk-ui/components/TabLayout';

class Basic extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = { activePanelId: 'one' };
    }

    handleChange = (e, data) => {
        this.setState({ activePanelId: data.activePanelId });
    }

    render() {
        return (
            <TabLayout activePanelId={this.state.activePanelId} onChange={this.handleChange}>
                <TabLayout.Panel label="One" panelId="one" style={{ margin: 20 }}>
                    <List type="upper-alpha">
                        <List.Item>Lorem ipsum dolor sit amet</List.Item>
                        <List.Item>Excepteur sint occaecat cupidatat non proident</List.Item>
                        <List.Item>Sed ut perspiciatis unde omnis</List.Item>
                    </List>
                </TabLayout.Panel>
                <TabLayout.Panel label="Two" panelId="two" style={{ margin: 20 }}>
                    <P>
                        Lorem ipsum dolor sit amet, consectetur adipiscing{' '}
                        <Link to="staging.buttercup.rocks" openInNewContext>elit</Link>. In dictum
                        metus enim, ac ullamcorper ante condimentum at. Pellentesque habitant morbi
                        tristique senectus et netus et{' '}
                    </P>
                </TabLayout.Panel>
                <TabLayout.Panel label="Three" panelId="three" style={{ margin: 20 }}>
                    <DL>
                        <DL.Term>First Item</DL.Term>
                        <DL.Description>Description</DL.Description>
                        <DL.Term>Another Item</DL.Term>
                        <DL.Description>Description</DL.Description>
                    </DL>
                </TabLayout.Panel>
            </TabLayout>
        );
    }
}

export default Basic;
