/* eslint-disable prefer-template */
import React from 'react';
import { _ } from 'splunk-ui/util/i18n';
import Link from 'splunk-ui/components/Link';
import P from 'splunk-ui/components/Paragraph';

const docsUrl = 'http://docs.splunk.com/Documentation/Splunk/6.5.0/AdvancedDev/TranslateSplunk';
const linkToDocs = (
    <Link to={docsUrl}>
        {_('documentation')}
    </Link>
);
function Basic() {
    return (
        <div>
            <P>
                {_('Splunk software uses the gettext internationalization system.')}
            </P>
            <P>
                {_('See the') + ' '}
                {linkToDocs}
                {' ' + _('on translating Splunk.')}
            </P>
            <P>
                {_(
                    'Wrapping text to be translated in the `gettext` or `_` function does two ' +
                    'things. ' +
                    'First, it provides an extraction hook necessary to generate a messages.po ' +
                    'file of strings to be translated. ' +
                    'Second, it swaps out the string with the appropriate translation ' +
                    '(if available) at run time.'
                )}
            </P>
            <P>
                {_('Click `Show Code` above to see an example of how it works!')}
            </P>
        </div>
    );
}

export default Basic;
