export default function baseComponent({ componentName }) {
    return {
        // Stub of route in the docs app.
        [`docs/src/pages/${componentName}.jsx`]: `import React from 'react';
import DocExample from 'components/DocExample';
import DocPage from 'components/DocPage';
import Specs from 'components/Specs';
import docs from '!!docgen-loader!splunk-ui/components/${componentName}';
import packageData from 'splunk-ui/components/${componentName}/package.json';

const examples = require.context('splunk-ui/components/${componentName}/examples/');
const examplesRaw = require.context('!!raw-loader!splunk-ui/components/${componentName}/examples/');

function ${componentName}() {
    return (
        <DocPage {...packageData} description={docs[0].description}>
            <DocExample
                title="Basic"
                description="Basic example."
                code={examplesRaw('./Basic')}
                example={examples('./Basic').default}
            />
            <Specs docs={docs[0]} />
        </DocPage>
    );
}

export default ${componentName};
`,

        // Stub of css file.
        [`src/components/${componentName}/${componentName}.css`]: `.main {
    text-align: center;
    margin-top: 50px;
}
`,

        // Stub of test file.
        [`src/components/${componentName}/${componentName}.test.jsx`]: `/* eslint-env mocha */

import React from 'react';
import { assert } from 'chai';
import { mount } from 'enzyme';
import ${componentName} from './${componentName}';

describe('${componentName}', () => {
    it('renders', () => {
        const wrapper = mount(<${componentName} />);
        assert.ok(wrapper);
    });
});
`,

        // Stub of an example.
        [`src/components/${componentName}/examples/Basic.jsx`]: `import React from 'react';
import ${componentName} from 'splunk-ui/components/${componentName}';

function Basic() {
    return <${componentName} />;
}

export default Basic;
`,

        // Component meta data.
        [`src/components/${componentName}/package.json`]: `{
    "name": "${componentName}",
    "main": "./${componentName}.jsx",
    "status": 1
}
`,
    };
}
