/* eslint-disable max-len */

import { extend } from 'lodash';
import baseComponent from './baseComponent';

export default function statelessComponent(options) {
    const { componentName } = options;
    return extend({
    // JavaScript file contents
        [`src/components/${componentName}/${componentName}.jsx`]: `import React, { PropTypes } from 'react';
import { createTestHook } from 'splunk-ui/util/testSupport';
import toClassName from 'splunk-ui/util/toClassName';
import css from './${componentName}.css';

const propTypes = {
    className: PropTypes.string,
    name: PropTypes.string.isRequired,
};

const defaultProps = {
    name: 'Joe',
};

export default function ${componentName}(props) {
    const { className, name, ...otherProps } = props;
    return (
        <div
            className={toClassName(css.main, className)}
            {...createTestHook(__filename)}
            {...otherProps}
        >
            Hello {name}
        </div>
    );
}

${componentName}.propTypes = propTypes;
${componentName}.defaultProps = defaultProps;
`,
    }, baseComponent(options));
}
