import fs from 'fs';

/**
 * Promisified version of fs#writeFile
 *
 * @param
 * @returns Promise
 */
export default function writeFile(file, data = '', options = {}) {
    return new Promise((resolve, reject) => {
        fs.writeFile(file, data, options, (err) => {
            if (err) reject(err);
            else resolve();
        });
    });
}
